        \item[(Product)] If $A_i \in \operatorname{SP}(n_i)$ for $i=1,2$ then $\conleyzehnder(A_1 \directsum A_2) = \conleyzehnder(A_1) + \conleyzehnder(A_2)$;
        \item[(Loop)] If $B \in C(S^1, \operatorname{Sp}(2n))$ and $B(0) = B(1) = \idm$ then $\conleyzehnder(B A) = \conleyzehnder(A) + 2 \maslov(B)$.
        \item[(Signature)] If $S$ is a symmetric nondegenerate $2n \times 2n$-matrix with operator norm $\p{||}{}{S} < 2 \pi$ and $A(t) = \exp(J_0 S t)$, then $\conleyzehnder(A) = \frac{1}{2} \signature (S)$;
        \item[(Determinant)] ${n - \conleyzehnder(A)}$ is even if and only if $\det (\idm - A(1)) > 0$;
        \item[(Inverse)] $\conleyzehnder(A ^{-1}) = \conleyzehnder (A^T) = - \conleyzehnder(A)$.
    \end{description}

